$(document).ready(function(){$(".toggle__btn").click(function(){$(".menu").toggleClass("active")}),document.querySelector(".copyBtn").addEventListener("click",function(e){var c=document.querySelector(".copyCode"),o=document.createRange();o.selectNode(c),window.getSelection().addRange(o);try{var t=document.execCommand("copy"),n=t?"скопирован":"не скопирован";console.log("Текст был "+n)}catch(e){console.log("Текст не скопирован")}window.getSelection().removeAllRanges()})});